  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const logoutHandler = () => {
    setIsLoggedIn(false)
  }

  return (
    <AuthContext.Provider>
      {props.children}
    </AuthContext.Provider>
  )
}

export default AuthContext;
